<!DOCTYPE html>
<html lang="en">
<head>
  <meta charset="utf-8" />
  <meta name="viewport" content="width=device-width, initial-scale=1, shrink-to-fit=no" />
  <title>Contact - Modern Business Portfolio</title>

  <!-- Favicon -->
  <link rel="icon" type="image/x-icon" href="assets/favicon.ico" />

  <!-- Google Fonts -->
  <link rel="preconnect" href="https://fonts.googleapis.com" />
  <link rel="preconnect" href="https://fonts.gstatic.com" crossorigin />
  <link href="https://fonts.googleapis.com/css2?family=Plus+Jakarta+Sans:wght@400;600;800&display=swap" rel="stylesheet" />

  <!-- Bootstrap Icons -->
  <link href="https://cdn.jsdelivr.net/npm/bootstrap-icons@1.8.1/font/bootstrap-icons.css" rel="stylesheet" />

  <!-- Bootstrap CSS -->
  <link href="https://cdn.jsdelivr.net/npm/bootstrap@5.3.0/dist/css/bootstrap.min.css" rel="stylesheet" />

  <style>
    body {
      background-color: #e9ecef;
      font-family: 'Plus Jakarta Sans', sans-serif;
    }

    .contact-card {
      transition: transform 0.3s ease, box-shadow 0.3s ease;
    }

    .contact-card:hover {
      transform: scale(1.01);
      box-shadow: 0 10px 25px rgba(0, 0, 0, 0.12);
    }

    .form-control {
      transition: box-shadow 0.3s ease, border-color 0.3s ease;
    }

    .form-control:hover {
      box-shadow: 0 0 0 0.1rem rgba(26, 26, 255, 0.1);
      border-color: #1A1AFF;
    }

    .form-control:focus {
      box-shadow: 0 0 0 0.2rem rgba(26, 26, 255, 0.25);
      border-color: #1A1AFF;
    }

    .btn-custom {
      background-color: #1A1AFF;
      border-color: #1A1AFF;
      transition: background-color 0.3s ease, transform 0.2s ease;
    }

    .btn-custom:hover {
      background-color: #0000e6;
      transform: translateY(-2px);
    }

    .icon-box {
      width: 50px;
      height: 50px;
      background: linear-gradient(135deg, #6c3df4, #c738f4);
      border-radius: 12px;
      display: flex;
      align-items: center;
      justify-content: center;
      margin: auto;
    }

    .success-msg, .error-msg {
      display: none;
    }

    .visible {
      display: block !important;
    }
  </style>
</head>

<body class="d-flex flex-column min-vh-100 justify-content-center">
  <main class="container">
    <div class="row justify-content-center">
      <div class="col-lg-6 col-md-8">
        <div class="bg-white rounded-4 shadow p-4 p-md-5 contact-card">
          <div class="text-center mb-4">
            <div class="icon-box mb-3">
              <i class="bi bi-envelope text-white fs-4"></i>
            </div>
            <h2 class="fw-bold">Get in touch</h2>
            <p class="text-muted mb-0">Let's work together!</p>
          </div>

          <form id="contactForm" name="contact" method="POST" data-netlify="true">
            <div class="form-floating mb-3">
              <input class="form-control" id="name" type="text" name="name" placeholder="Your Name" required />
              <label for="name">Full name</label>
              <div class="invalid-feedback">Name is required.</div>
            </div>

            <div class="form-floating mb-3">
              <input class="form-control" id="email" type="email" name="email" placeholder="email@example.com" required />
              <label for="email">Email address</label>
              <div class="invalid-feedback">Email is required.</div>
            </div>

            <div class="form-floating mb-3">
              <input class="form-control" id="phone" type="tel" name="phone" placeholder="(123) 456-7890" required />
              <label for="phone">Phone number</label>
              <div class="invalid-feedback">Phone is required.</div>
            </div>

            <div class="form-floating mb-3">
              <textarea class="form-control" id="message" name="message" placeholder="Your message..." style="height: 10rem" required></textarea>
              <label for="message">Message</label>
              <div class="invalid-feedback">Message is required.</div>
            </div>

            <!-- Feedback -->
            <div class="text-center success-msg text-success fw-bold mb-3" id="submitSuccessMessage">
              Form submitted successfully!
            </div>
            <div class="text-center error-msg text-danger fw-bold mb-3" id="submitErrorMessage">
              Something went wrong. Try again.
            </div>

            <div class="d-grid">
              <button class="btn btn-custom btn-lg text-white" type="submit" id="submitButton">Submit</button>
            </div>
          </form>

        </div>
      </div>
    </div>
  </main>

  <!-- Optional JS for custom form submission feedback -->
  <script>
    const form = document.getElementById('contactForm');
    const success = document.getElementById('submitSuccessMessage');
    const error = document.getElementById('submitErrorMessage');

    form.addEventListener('submit', function (e) {
      e.preventDefault();
      const data = new FormData(form);

      fetch('/', {
        method: 'POST',
        body: data,
        headers: { 'Accept': 'application/json' }
      }).then(response => {
        if (response.ok) {
          success.classList.add('visible');
          error.classList.remove('visible');
          form.reset();
        } else {
          throw new Error("Form submission failed");
        }
      }).catch(() => {
        error.classList.add('visible');
        success.classList.remove('visible');
      });
    });
  </script>
</body>
</html>

