        tabButtons.scrollBy({ left: -150, behavior: 'smooth' });
      });

      rightArrow.addEventListener('click', () => {
        tabButtons.scrollBy({ left: 150, behavior: 'smooth' });
      });

      tabButtons.addEventListener('scroll', updateArrowVisibility);

      panes.forEach(pane => {
        pane.addEventListener('scroll', () => updateScrollArrow(pane));
      });

      window.addEventListener('load', () => {
        updateArrowVisibility();
        panes.forEach(updateScrollArrow);
      });
      window.addEventListener('resize', updateArrowVisibility);
