    <script>
      const buttons = document.querySelectorAll('.custom-badge');
      const panes = document.querySelectorAll('.tab-pane');
      const tabButtons = document.querySelector('.tab-buttons');
      const leftArrow = document.querySelector('.left-arrow');
      const rightArrow = document.querySelector('.right-arrow');

      function updateArrowVisibility() {
        const scrollLeft = tabButtons.scrollLeft;
        const maxScrollLeft = tabButtons.scrollWidth - tabButtons.clientWidth;
        leftArrow.classList.toggle('visible', scrollLeft > 0);
        rightArrow.classList.toggle('visible', scrollLeft < maxScrollLeft - 1);
      }

      function updateScrollArrow(pane) {
        const arrow = pane.querySelector('.down-arrow');
        const scrollTop = pane.scrollTop;
        const scrollHeight = pane.scrollHeight;
        const clientHeight = pane.clientHeight;
        const shouldShow = scrollTop + clientHeight < scrollHeight - 10;
        arrow.classList.toggle('visible', shouldShow);
      }

      buttons.forEach(button => {
        button.addEventListener('click', () => {
          buttons.forEach(btn => btn.classList.remove('active'));
          panes.forEach(pane => pane.classList.remove('active'));
          button.classList.add('active');
          const targetPane = document.getElementById(button.dataset.target);
          targetPane.classList.add('active');
          updateScrollArrow(targetPane);
        });
      });

      leftArrow.addEventListener('click', () => {
        tabButtons.scrollBy({ left: -150, behavior: 'smooth' });
      });

      rightArrow.addEventListener('click', () => {
        tabButtons.scrollBy({ left: 150, behavior: 'smooth' });
      });

      tabButtons.addEventListener('scroll', updateArrowVisibility);

      panes.forEach(pane => {
        pane.addEventListener('scroll', () => updateScrollArrow(pane));
      });

      window.addEventListener('load', () => {
        updateArrowVisibility();
        panes.forEach(updateScrollArrow);
      });
      window.addEventListener('resize', updateArrowVisibility);
    </script>
